/*BudgetMaria.getResDocSubmitList*/ SELECT d.res_doc_seq AS 'consDocSeq' , d.doc_seq AS 'docSeq' , d.doc_seq AS 'docSeq' , IFNULL(r.c_ridocfullnum, '') AS 'docNo' , d.doc_status AS 'docStatus'
, date_format(gwlink.appr_end_date, '%Y-%m-%d') AS 'docDate' , d.resdoc_note AS 'consdocNote' , d.resdoc_note AS 'resdocNote' , d.expend_date AS 'expendDate' , d.comp_name AS 'compName' , d.dept_name AS 'deptName' , d.emp_name AS 'empName' , d.send_emp_seq AS 'sendEmpSeq' , d.send_date AS 'sendDate' , d.send_emp_name AS 'sendName' , r.c_riaftertitle AS 'docTitle' , amt.resDocAmt AS 'resDocAmt' , amt.resDocTaxAmt AS 'resDocTaxAmt' , amt.resDocStdAmt AS 'resDocStdAmt' , d.erp_send_yn AS 'erpSendYN' , 'EA' AS 'eaType' , IFNULL(ds.submit_yn, 'N') AS 'submitYn' , concat(date_format(ds.reg_date, '%Y-%m-%d'), '-', lpad(ds.reg_no, 4, '0')) AS 'regNo' , ds.return_yn AS 'returnYn' , ds.return_reason AS 'returnReason' , ds.return_date AS 'returnDate' FROM neos.t_exnp_resdoc d INNER JOIN neos.a_recordinfo r ON d.doc_seq = r.c_dikeycode INNER JOIN ( SELECT c_dikeycode, MIN(c_diwriteday) AS c_diwriteday FROM neos.a_draftinfo WHERE IFNULL(c_distatus, '008') != 'd' GROUP BY c_dikeycode ) i ON r.c_dikeycode = i.c_dikeycode INNER JOIN ( SELECT res_doc_seq , SUM(IFNULL(budget_amt, 0)) AS resDocAmt , SUM(IFNULL(budget_tax_amt, 0)) AS resDocTaxAmt , SUM(IFNULL(budget_std_amt, 0)) AS resDocStdAmt FROM neos.t_exnp_resbudget GROUP BY res_doc_seq ) amt ON d.res_doc_seq = amt.res_doc_seq INNER JOIN neos.erpgwlink gwlink ON gwlink.appr_dikey = d.doc_seq LEFT JOIN res_doc_submit ds ON d.res_doc_seq = ds.resDocSeq WHERE d.comp_seq = #{compSeq}
AND d.emp_seq = #{empSeq}
AND d.dept_seq = #{deptSeq}
AND d.doc_seq IS NOT NULL AND IFNULL(doc_status, '008') IN ( '001', '002', '003', '004','007', '008', '009','20', '30', '90' ) AND doc_status in ('008', '009', '90') AND LEFT(requ_date,8) BETWEEN #{frDt} AND #{toDt}
AND c_riaftertitle LIKE CONCAT('%', IFNULL(#{docTitle}, ''), '%')
AND c_ridocfullnum LIKE CONCAT('%', IFNULL(#{docNo}, ''), '%')
AND IFNULL(ds.submit_yn, 'N') = #{submitYn}
ORDER BY ${order}
ORDER BY expend_date DESC

