mapper.update:
UPDATE DJ_EPIS.V_DOC_INFO SET DOC_TITLE = #{docTitle}, DOC_CONTENT = #{docContent}, APPROVE_STAT_CODE_DESC = #{approveStatCodeDesc}, APPROVE_STAT_CODE = #{approveStatCode},
LAST_APPROVE_EMP_SEQ = #{lastApproveEmpSeq}, LAST_APPROVE_EMP_NAME = #{lastApproveEmpName}, LAST_APPROVE_POSITION_NAME = #{lastApprovePositionName}, LAST_APPROVE_DUTY_NAME = #{lastApproveDutyName},
REPTIT_DRFT_YN = 'Y', REPTIT_DRFT_DT = NOW(), MOD_EMP_SEQ = #{empSeq}, MOD_DATE = NOW() WHERE DOC_ID = #{docId}

mapper.select:
/* getCmCodeInfo */
SELECT CC.* FROM DJ_EPIS.V_COM_CODE CC WHERE 1=1
AND CC.CM_CODE_ID = #{cmCodeId}
AND CC.CM_CODE_NM = '상신' AND CC.CM_CODE_NM = '결재' AND CC.CM_CODE_NM = '반려' AND CC.CM_CODE_NM = '재상신' AND CC.CM_CODE_NM = '회수' AND CC.CM_CODE_NM = '최종결재'
