mapper.select:
select

*

from

opnet_day_cost_batch

where

date between #{start_dt} and #{end_dt}

and

prodCd not in ('d047', 'b034')


    AND SIDOCD =#{SIDOCD}


    AND PRODCD =#{PRODCD}

SELECT *
from
(SELECT A.*,
B.detail_code, B.detail_name FROM neos.t_exnp_resdoc A ,neos.t_co_code_detail_multi B WHERE A.doc_status = B.detail_code AND code = 'COM109' AND lang_code = 'kr' AND detail_code in(002, 008)
AND dept_seq =#{dept_seq}
AND doc_no like concat ('%',#{docNo},'%')
) R,
(SELECT
A.biz_common_seq, A.writer_emp_seq, A.writer_emp_name, A.project_name, A.pjt_cd, A.active, A.create_date, A.res_doc_seq,
B.biz_common_seq as biz_common_seqq, B.emp_name as biz_emp_name, B.emp_seq, B.date, B.stime, B.etime, B.location, B.biz_car_yn, B.amt_pay, B.bank_name, B.bank_seq, B.depositor, B.purpose, B.bank_nb, B.biz_trip_no, B.trade_seq, B.index
from
cust_epis.dj_hr_biz_common A, cust_epis.dj_hr_biz_city B
where A.biz_common_seq = B.biz_common_seq
and active= #{active}
AND emp_name like concat ('%',#{emp_name},'%')
AND purpose like concat ('%',#{purpose},'%')
AND date between #{txtFrDt} and #{txtToDt}
) V
WHERE R.res_doc_seq = V.res_doc_seq
LIMIT ${skip} , ${pageSize}

SELECT *
from
(SELECT A.*,
B.detail_code, B.detail_name FROM neos.t_exnp_resdoc A ,neos.t_co_code_detail_multi B WHERE A.doc_status = B.detail_code AND code = 'COM109' AND lang_code = 'kr' AND detail_code in(002, 008)
AND dept_seq =#{dept_seq}
AND doc_no like concat ('%',#{docNo},'%')
) R,
(select A.active, a.biz_common_seq, a.res_doc_seq as resDocSeq, b.trade_seq, b.budget_seq, b.emp_seq as btEmpSeq, b.emp_name as btEmpNm, b.sdate, b.edate, b.purpose, b.location, b.trip_no_onnara, c.trafic_cost, c.room_cost, c.day_cost, c.food_cost, c.etc_cost, c.total_cost, c.biz_day, c.sub_seq, c.file_seq2
from cust_epis.dj_hr_biz_common a, cust_epis.dj_hr_biz_outside b, cust_epis.dj_hr_biz_out_sub c where a.biz_common_seq = b.biz_common_seq and b.biz_common_seq = c.biz_common_seq
and active= #{active}
AND emp_name like concat ('%',#{emp_name},'%')
AND purpose like concat ('%',#{purpose},'%')
AND biz_day between #{txtFrDt} and #{txtToDt}
) V
WHERE R.res_doc_seq = V.resdocseq
LIMIT ${skip} , ${pageSize}

