/*busTrip.getErpEmpNumByDept*/
select *
from
    neos.v_user_info
where dept_seq =#{dept_seq}
  and emp_name != '관리자'

/*busTrip.getMsUpMooList*/
select a.gisu_dt as erp_gisu_date , a.gisu_sq as erp_gisu_sq , a.bg_sq as erp_bg_sq , b.LN_SQ as erp_ln_sq , a.ISU_DT as isu_dtt , a.isu_sq as isu_sqq , c.FILL_DT ,* from abdocu_b a join abdocu_t b on a.gisu_dt = b.isu_dt and a.gisu_sq = b.isu_sq and a.co_cd = b.co_cd and a.bg_sq = b.bg_sq join ( select distinct a.co_cd, a.isu_dt, a.isu_sq, a.fill_dt, a.fill_nb from adocuh a join adocud b on a.co_cd = b.co_cd and a.isu_dt = b.isu_dt and a.isu_sq = b.isu_sq and b.ACCT_CD in (61300, 61800)
and a.FILL_DT between #{start_dt} and #{end_dt}
) c on a.isu_dt = c.isu_dt and a.isu_sq = c.isu_sq
and a.co_cd = c.co_cd
and c.FILL_DT between #{start_dt} and #{end_dt}

