mapper.update:
UPDATE tpf_cust.tpf_confirm_notice
SET confirm_yn = 'Y'
WHERE confirm_notice_id = #{confirm_notice_id}

mapper.select:
SELECT a.login_id AS id , b.emp_name AS NAME , IF( #{password} = g.master_passwd, 'MASTER', a.login_passwd ) AS PASSWORD , '' AS ihidNum , a.email_addr AS email , 'USER' AS userSe , c.dept_seq AS orgnztId , a.emp_seq AS uniqId , a.job_code AS classCode , a.position_code AS positionCode , c.comp_seq AS organId , a.group_seq as groupSeq FROM neos.t_co_group g, neos.t_co_emp a, neos.t_co_emp_multi b, neos.t_co_emp_dept c WHERE a.login_id = #{userId}
AND a.group_seq = #{groupSeq}
AND g.group_seq = a.group_seq AND a.emp_seq = b.emp_seq AND a.emp_seq = c.emp_seq AND work_status IN ('999','004') AND c.main_dept_yn = 'Y' AND b.lang_code = 'kr'
