/* resalphaG20.selectPdfErrorDocs */ SELECT LEFT(SUBSTRING_INDEX(RET_MSG, 'error|', -1), (LENGTH(SUBSTRING_INDEX(RET_MSG, 'error|', -1)) - 10)) AS ERROR_MSG , A.* , B.* , C.* FROM CUST_EPIS.C_PDF_DUZON_APPDOC A INNER JOIN NEOS.A_DOCINFO B ON A.REP_ID = B.C_DIKEYCODE INNER JOIN NEOS.T_EXNP_RESDOC C ON A.REP_ID = C.DOC_SEQ WHERE A.RET_MSG != '성공' AND A.EC_DT > '2020-07-29' AND C.create_date BETWEEN #{fromDate} and #{toDate}
AND C.doc_no like CONCAT('%',#{searchWord},'%')
ORDER BY A.REQ_DT DESC

/* resalphaG20.selectAdocuAndDailyDocs */ select a.*, b.*, c.emp_name, d.pdf_name, d.pdf_path from neos.erpgwlink a inner join neos.a_docinfo b on a.appr_dikey = b.c_dikeycode inner join neos.t_co_emp_multi c on b.create_seq = c.emp_seq and lang_code = 'kr' inner join cust_epis.c_pdf_duzon_appdoc d on d.rep_id = a.appr_dikey where appr_status = '008'
and docx_numb = #{docx_numb}
and docx_numb like concat('DAILYSCD', #{fill_dt}, '%')
ORDER BY docx_numb asc

/* resalphaG20.GetCardListFullList */ /* 법인카드 승인내역 전체 현황 조회 */ SELECT final.* ,CASE WHEN (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) = 0 THEN '정산' WHEN (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) != 0 THEN '미정산' END isCal FROM ( SELECT CASE card.georae_stat WHEN '1' THEN 'N' WHEN '0' THEN 'Y' ELSE card.georae_stat END AS georaeStat /* 구분 ( 승인 / 취소 ) */ , ( CASE WHEN IFNULL(georae_stat, '') = 'Y' OR IFNULL(georae_stat, '') = '0' THEN '취소' ELSE '승인' END ) AS georaeStatName , card.georae_coll AS georaeColl , card.auth_date AS authDate /* 승인일시 */ , card.auth_time AS authTime , card.auth_num AS authNum /* 승인번호 */ , card.merc_name AS partnerName /* 사용처 */ , card.merc_saup_no AS partnerNo /* 사업자번호 */ , c.card_name AS cardName /* 카드명 */ , card.card_num AS cardNum /* 카드번호 */ , card.request_amount AS reqAmt /* 금액 */ , card.amt_md_amount AS stdAmt /* 공급가액 */ , card.vat_md_amount AS vatAmt /* 부가세 */ , IFNULL(send.trade_amt, 0) AS tradeAmt /* 실제결의금액 */ , card.ser_amount AS serAmount /* 서비스 금액 */ , '' AS cardPublicYn /* 공개범위 여부 */ , card.sync_id AS syncId /* 카드 내역 고유 아이디 */ , c.card_code AS cardCode /* 카드 코드 */ , IFNULL(send.form_seq, '') AS formSeq , IFNULL(send.doc_seq, '') AS docSeq , IFNULL(send.doc_emp_name, '') AS docEmpName , IFNULL(send.doc_emp_name, '') AS sendEmpName , IFNULL(send.doc_no, '') AS docNo , IFNULL(send.doc_title, '') AS docTitle , IFNULL(send.doc_status, '') AS docStatus , IFNULL(send.use_code, '') AS sendYN , IFNULL(send.use_code, '') AS send_yn , IFNULL(send.use_code, '') AS approYN , IFNULL(send.use_code, '') AS appro_yn , IFNULL(notUse.not_use_emp_name, '') AS notUseEmpName , IFNULL(notUse.use_code, 'Y') AS useYN , IFNULL(notUse.use_code, 'Y') AS use_yn , IFNULL(receive.transfer_name, '') AS transferName , IFNULL(receive.use_code, '') AS receiveYN , IFNULL(receive.use_code, '') AS receive_yn , IFNULL(send.reason, '') AS reason , IFNULL(send.selfAmt, 0) AS selfAmt , send.create_date AS createDate , IFNULL(send.emp_seq, '') AS empSeq , IFNULL(send.emp_name, '') AS empName , IFNULL(send.ad_reason, '') AS ad_reason , IFNULL(send.adjustAmt, 0) AS adjustAmt , send.ad_create_date AS ad_create_date , IFNULL(send.ad_emp_seq, '') AS ad_emp_seq , IFNULL(send.ad_emp_name, '') AS ad_emp_name , IFNULL(( select IFNULL(SUM(trade_amt), 0) as trade_amt from neos.t_ex_card_aq_tmp a inner join neos.t_exnp_restrade b on a.sync_id = b.interface_seq inner join neos.t_exnp_resdoc c on c.res_doc_seq = b.res_doc_seq and doc_status = '008' where a.sync_id = card.sync_id and interface_type = 'card' group by sync_id ), 0) as totalAmt FROM neos.t_ex_card_aq_tmp card LEFT JOIN neos.t_ex_card c ON card.card_num = replace(c.card_num,'-','') LEFT JOIN ( SELECT DISTINCT 'Y' AS use_code , c.sync_id , IFNULL(d.doc_seq, '') AS doc_seq , IFNULL((SELECT emp_name FROM neos.t_co_emp_multi WHERE lang_code = 'kr' AND emp_seq = d.emp_seq), '') AS doc_emp_name , d.doc_no , IFNULL(ar.c_riaftertitle, ta.doc_title) AS doc_title , doc_status , IFNULL(ta.form_id, '') form_seq , t.trade_amt , sp.reason , sp.selfAmt , sp.create_date , sp.emp_seq , sp.emp_name , sa.reason AS ad_reason , sa.adjustAmt , sa.create_date AS ad_create_date , sa.emp_seq AS ad_emp_seq , sa.emp_name AS ad_emp_name FROM neos.t_ex_card_aq_tmp c INNER JOIN neos.t_exnp_restrade t ON c.sync_id = t.interface_seq INNER JOIN neos.t_exnp_resdoc d ON d.res_doc_seq = t.res_doc_seq LEFT JOIN neos.a_recordinfo ar ON d.doc_seq = ar.c_dikeycode LEFT JOIN neos.teag_appdoc ta ON d.doc_seq = ta.doc_id LEFT JOIN CUST_EPIS.res_self_pay_card sp ON c.sync_id = sp.sync_id LEFT JOIN CUST_EPIS.res_adjust_pay_card sa ON c.sync_id = sa.sync_id WHERE d.doc_seq IS NOT NULL AND IFNULL(t.interface_type, '') = 'card' AND IFNULL(d.doc_status, '008') IN ( '001', '002', '003', '004', '008', '009','20', '30', '90' ) AND IFNULL(del_yn, 'N') = 'N' ) send ON card.sync_id = send.sync_id LEFT JOIN ( SELECT 'Y' AS use_code , sync_id AS sync_id , transfer_name AS transfer_name FROM neos.t_ex_card_transfer t WHERE IFNULL(use_yn, 'Y') = 'Y' AND IFNULL(receive_seq, '') != '' ) receive ON card.sync_id = receive.sync_id LEFT JOIN ( SELECT 'N' AS use_code , sync_id AS sync_id , IFNULL((SELECT emp_name FROM neos.t_co_emp_multi WHERE lang_code = 'kr' AND emp_seq = aq.use_yn_emp_seq), '') AS not_use_emp_name FROM neos.t_ex_card_transfer aq WHERE IFNULL(use_yn, 'N') = 'N' ) notUse ON card.sync_id = notUse.sync_id WHERE card.auth_date BETWEEN #{fromDate} AND #{toDate} GROUP BY card.card_num, card.auth_date, card.auth_num, card.auth_time, card.georae_stat, card.georae_coll ORDER BY auth_date DESC, auth_time DESC ) as final
WHERE (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) = 0
WHERE (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) != 0
LIMIT ${skip} , ${pageSize}

/* resalphaG20.selectCardFullListTotal */ /* 법인카드 승인내역 전체 현황 개수 조회 */ SELECT count(*) FROM ( SELECT CASE card.georae_stat WHEN '1' THEN 'N' WHEN '0' THEN 'Y' ELSE card.georae_stat END AS georaeStat /* 구분 ( 승인 / 취소 ) */ , ( CASE WHEN IFNULL(georae_stat, '') = 'Y' OR IFNULL(georae_stat, '') = '0' THEN '취소' ELSE '승인' END ) AS georaeStatName , card.georae_coll AS georaeColl , card.auth_date AS authDate /* 승인일시 */ , card.auth_time AS authTime , card.auth_num AS authNum /* 승인번호 */ , card.merc_name AS partnerName /* 사용처 */ , card.merc_saup_no AS partnerNo /* 사업자번호 */ , c.card_name AS cardName /* 카드명 */ , card.card_num AS cardNum /* 카드번호 */ , card.request_amount AS reqAmt /* 금액 */ , card.amt_md_amount AS stdAmt /* 공급가액 */ , card.vat_md_amount AS vatAmt /* 부가세 */ , IFNULL(send.trade_amt, 0) AS tradeAmt /* 실제결의금액 */ , card.ser_amount AS serAmount /* 서비스 금액 */ , '' AS cardPublicYn /* 공개범위 여부 */ , card.sync_id AS syncId /* 카드 내역 고유 아이디 */ , c.card_code AS cardCode /* 카드 코드 */ , IFNULL(send.form_seq, '') AS formSeq , IFNULL(send.doc_seq, '') AS docSeq , IFNULL(send.doc_emp_name, '') AS docEmpName , IFNULL(send.doc_emp_name, '') AS sendEmpName , IFNULL(send.doc_no, '') AS docNo , IFNULL(send.doc_title, '') AS docTitle , IFNULL(send.doc_status, '') AS docStatus , IFNULL(send.use_code, '') AS sendYN , IFNULL(send.use_code, '') AS send_yn , IFNULL(send.use_code, '') AS approYN , IFNULL(send.use_code, '') AS appro_yn , IFNULL(notUse.not_use_emp_name, '') AS notUseEmpName , IFNULL(notUse.use_code, 'Y') AS useYN , IFNULL(notUse.use_code, 'Y') AS use_yn , IFNULL(receive.transfer_name, '') AS transferName , IFNULL(receive.use_code, '') AS receiveYN , IFNULL(receive.use_code, '') AS receive_yn , IFNULL(send.reason, '') AS reason , IFNULL(send.selfAmt, 0) AS selfAmt , send.create_date AS createDate , IFNULL(send.emp_seq, '') AS empSeq , IFNULL(send.emp_name, '') AS empName , IFNULL(send.ad_reason, '') AS ad_reason , IFNULL(send.adjustAmt, 0) AS adjustAmt , send.ad_create_date AS ad_create_date , IFNULL(send.ad_emp_seq, '') AS ad_emp_seq , IFNULL(send.ad_emp_name, '') AS ad_emp_name , IFNULL(( select IFNULL(SUM(trade_amt), 0) as trade_amt from neos.t_ex_card_aq_tmp a inner join neos.t_exnp_restrade b on a.sync_id = b.interface_seq inner join neos.t_exnp_resdoc c on c.res_doc_seq = b.res_doc_seq and doc_status = '008' where a.sync_id = card.sync_id and interface_type = 'card' group by sync_id ), 0) as totalAmt FROM neos.t_ex_card_aq_tmp card LEFT JOIN neos.t_ex_card c ON card.card_num = replace(c.card_num,'-','') LEFT JOIN ( SELECT DISTINCT 'Y' AS use_code , c.sync_id , IFNULL(d.doc_seq, '') AS doc_seq , IFNULL((SELECT emp_name FROM neos.t_co_emp_multi WHERE lang_code = 'kr' AND emp_seq = d.emp_seq), '') AS doc_emp_name , d.doc_no , IFNULL(ar.c_riaftertitle, ta.doc_title) AS doc_title , doc_status , IFNULL(ta.form_id, '') form_seq , t.trade_amt , sp.reason , sp.selfAmt , sp.create_date , sp.emp_seq , sp.emp_name , sa.reason AS ad_reason , sa.adjustAmt , sa.create_date AS ad_create_date , sa.emp_seq AS ad_emp_seq , sa.emp_name AS ad_emp_name FROM neos.t_ex_card_aq_tmp c INNER JOIN neos.t_exnp_restrade t ON c.sync_id = t.interface_seq INNER JOIN neos.t_exnp_resdoc d ON d.res_doc_seq = t.res_doc_seq LEFT JOIN neos.a_recordinfo ar ON d.doc_seq = ar.c_dikeycode LEFT JOIN neos.teag_appdoc ta ON d.doc_seq = ta.doc_id LEFT JOIN CUST_EPIS.res_self_pay_card sp ON c.sync_id = sp.sync_id LEFT JOIN CUST_EPIS.res_adjust_pay_card sa ON c.sync_id = sa.sync_id WHERE d.doc_seq IS NOT NULL AND IFNULL(t.interface_type, '') = 'card' AND IFNULL(d.doc_status, '008') IN ( '001', '002', '003', '004', '008', '009','20', '30', '90' ) AND IFNULL(del_yn, 'N') = 'N' ) send ON card.sync_id = send.sync_id LEFT JOIN ( SELECT 'Y' AS use_code , sync_id AS sync_id , transfer_name AS transfer_name FROM neos.t_ex_card_transfer t WHERE IFNULL(use_yn, 'Y') = 'Y' AND IFNULL(receive_seq, '') != '' ) receive ON card.sync_id = receive.sync_id LEFT JOIN ( SELECT 'N' AS use_code , sync_id AS sync_id , IFNULL((SELECT emp_name FROM neos.t_co_emp_multi WHERE lang_code = 'kr' AND emp_seq = aq.use_yn_emp_seq), '') AS not_use_emp_name FROM neos.t_ex_card_transfer aq WHERE IFNULL(use_yn, 'N') = 'N' ) notUse ON card.sync_id = notUse.sync_id WHERE card.auth_date BETWEEN #{fromDate} AND #{toDate} GROUP BY card.card_num, card.auth_date, card.auth_num, card.auth_time, card.georae_stat, card.georae_coll ORDER BY auth_date DESC, auth_time DESC ) as final
WHERE (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) = 0
WHERE (final.reqAmt - final.totalAmt + final.selfAmt - final.adjustAmt) != 0

