/* resalphag20.getAdocuTradeInfo */ SELECT AA.* FROM ( SELECT G.* ,D.appr_dikey as ADOCU_APPR_DIKEY ,D.appr_status as ADOCU_DOC_STATUS ,A.res_doc_seq as RES_DOC_SEQ ,A.res_seq as RES_DOC ,A.erp_dept_name as ERP_DEPT_NAME ,A.erp_emp_seq as ERP_EMP_SEQ ,A.erp_emp_name as ERP_EMP_NAME ,A.res_date as RES_DATE ,B.doc_no as DOC_NO ,B.doc_seq as C_DIKEYCODE ,ifnull(E.sms_sender, '') as SMS_SENDER ,ifnull(E.e_nm, '') as SMS_NAME ,ifnull(E.e_phone_num, '') as SMS_PHONE ,ifnull(H.key, '') as SEND_YN ,ifnull(F.c_ridocfullnum, '') as ADOCU_DOC_NO FROM cust_epis.res_adocu_tmp G INNER JOIN neos.t_exnp_reshead A ON A.erp_gisu_date = G.ISU_DT and A.erp_gisu_sq = G.ISU_SQ INNER JOIN neos.t_exnp_resdoc B ON A.res_doc_seq = B.res_doc_seq INNER JOIN neos.a_docinfo C ON B.doc_seq = C.c_dikeycode LEFT JOIN neos.erpgwlink D ON docx_numb = CONCAT('DJADOCUEA_', G.ISU_DT, '_', G.ISU_SQ) LEFT JOIN neos.a_recordinfo F ON D.appr_dikey = F.c_dikeycode LEFT JOIN cust_epis.sms_info E ON E.tr_cd = G.TR_CD LEFT JOIN cust_epis.sms_log H ON H.ISU_DT = G.ISU_DT and H.ISU_SQ = G.ISU_SQ and H.LN_SQ = G.LN_SQ ORDER BY FILL_DT DESC ) as AA
WHERE AA.SEND_YN = ''
WHERE AA.SEND_YN != ''
LIMIT ${skip} , ${pageSize}

/* resalphag20.getAdocuTradeInfoTotal */ SELECT COUNT(AA.SEND_YN) as cnt FROM ( SELECT ifnull(H.key, '') as SEND_YN FROM cust_epis.res_adocu_tmp G INNER JOIN neos.t_exnp_reshead A ON A.erp_gisu_date = G.ISU_DT and A.erp_gisu_sq = G.ISU_SQ INNER JOIN neos.t_exnp_resdoc B ON A.res_doc_seq = B.res_doc_seq INNER JOIN neos.a_docinfo C ON B.doc_seq = C.c_dikeycode LEFT JOIN neos.erpgwlink D ON docx_numb = CONCAT('DJADOCUEA_', G.ISU_DT, '_', G.ISU_SQ) LEFT JOIN cust_epis.sms_info E ON E.tr_cd = G.TR_CD LEFT JOIN cust_epis.sms_log H ON H.ISU_DT = G.ISU_DT and H.ISU_SQ = G.ISU_SQ and H.LN_SQ = G.LN_SQ ) as AA
WHERE AA.SEND_YN = ''
WHERE AA.SEND_YN != ''

mapper.insert:
/*resalphag20.saveInfoAboutSms*/
insert into cust_epis.sms_info(e_id_num, e_phone_num, e_nm , sms_sender, tr_cd)
values (#{e_id_num}, #{e_phone_num},#{e_nm}, #{sms_sender}, #{tr_cd})
on duplicate key update e_phone_num = #{e_phone_num}

mapper.select:
/*resalphag20.selectCardAlamBatchLog */
SELECT sync_id as syncId ,card_name as cardName ,card_num as cardNum ,partner_name as partnerName ,partner_num as partnerNum ,auth_date as authDate ,send_YN as sendYN ,create_date as createDate ,modify_date as modifyDate FROM cust_epis.res_card_alam_log WHERE use_yn = 'Y'
and A.send_YN = #{sendYN}

/*resalphag20.selectSmsMessage */

SELECT message
FROM CUST_EPIS.res_sms_message_template limit 1
