sqlMap.select:
/* AcCommonDAO.acErpSystemInfo - mariadb */ SELECT `database_type` AS `DATABASE_TYPE` /* 데이터베이스 타입 */ , `driver` AS `DRIVER` /* 드라이버 */ , `url` AS `URL` /* URL */ , `userid` AS `USERID` /* 접속계정 */ , `password` AS `PASSWORD` /* 접속비번 */ , `erp_type_code` AS ERPTYPECODE /* 연동ERP구분 */ FROM neos.t_co_erp /* 회사 검색조건 */
WHERE comp_seq = #loginVO.compSeq#
WHERE comp_seq = (select comp_seq from neos.t_co_comp order by comp_seq limit 1)
/* 연동 ERP 검색조건 [ ac : 회계 / etc : 기타 ] */ AND ( achr_gbn = 'ac' OR achr_gbn = 'etc' ) /* 정렬순서 적용 ( 회계우선 ) */ ORDER BY achr_gbn LIMIT 1

/* AcCommonDAO.acErpSystemInfoTest - mariadb */ SELECT `database_type` AS `DATABASE_TYPE` /* 데이터베이스 타입 */ , `driver` AS `DRIVER` /* 드라이버 */ , `url` AS `URL` /* URL */ , `userid` AS `USERID` /* 접속계정 */ , `password` AS `PASSWORD` /* 접속비번 */ , `erp_type_code` AS ERPTYPECODE /* 연동ERP구분 */ FROM t_co_erp /* 회사 검색조건 */
WHERE comp_seq = #loginVO.compSeq#
WHERE comp_seq = (select comp_seq from neos.t_co_comp order by comp_seq limit 1)
/* 연동 ERP 검색조건 [ ac : 회계 / etc : 기타 ] */ AND ( achr_gbn = 'ac' OR achr_gbn = 'etc' ) /* 정렬순서 적용 ( 회계우선 ) */ ORDER BY achr_gbn LIMIT 1
